import React, { Component } from 'react';
import GridCard from "./GridCard";
import ActivityBar from './ActivityBar/ActivityBar';
import "../custom.css";

class Container extends Component {

    constructor(props) {
        super(props);
        this.setState({items : this.props.items});
    }

    static getDerivedStateFromProps(nextProps) {    
        return {
          items: nextProps.items,
        }
      }

    getFilteredData =(filteredItems)=>{
        console.log("container filter data",filteredItems);
       this.setState({items : filteredItems});
       console.log("container filter state",this.state.items);
       //this.props.items=filteredItems;
    }

    render() {
        let items = this.props.items;
        console.log("activity state",this.state.items);
        const renderItem = <div className="row margin-auto">{
            this.state.items.map((foodItem,index) => 
            <GridCard
            item = {foodItem}
            index = {index}
            />
            )}
         </div>
        return([
            <div className="jumborton"><ActivityBar items={items}
            getFilteredData= {this.getFilteredData}/></div>,
            renderItem
        ]
        );
    }
}

export default Container;
