import React, { Component } from 'react';
import * as api from '../Api/api';
import Spinner from "../common/Spinner";
import GridCard from "./GridCard";
import ActivityBar from './ActivityBar';
class Dashboard extends Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [],
            loading: true
        };
    }

    componentDidMount() {
        api.getAllItems()
        .then((response) => {
            this.setState({
                items: response.data.menu.items,
                loading: false
            });
            console.log("items", this.state.items);
            // this.props.loadFoodItems(items.menu);
            // this.props.filterMenu(items.menu);
        })
        .catch(error => {
            console.log(error);
            // this.props.loadFoodItems([]);
            // this.props.filterMenu([]);
        });
    }

    render() {
        const renderItem = this.state.items.map((foodItem,index) => 
            <GridCard
            item = {foodItem}
            index = {index}
            />
        );
        return (
                <div className="row">{
                    this.state.loading ? 
                        <div className="col-lg-12 text-center"><Spinner/></div>
                    :[
                        (<ActivityBar/>),
                    {renderItem}]
                }
                </div>
        );
    }
}
export default Dashboard;
