import React, { Component, useState } from 'react';
import "./ActivityBar/activity.css";
import { Button, Modal} from 'react-bootstrap';

class OrderSummaryModal extends Component {

    constructor(props) {
        super(props);
    }

    componentWillMount() {
        this.setState({showModal:this.props.showModal});
    }
    handleModelClose = () => {
        this.setState({showModal:false});
    }
    getProductTotalPrice = (item) => {
        return item.quantity*item.price;
    }


    render() {

        return(
            <Modal show={this.state.showModal} onHide={this.handleModelClose}>
            <Modal.Header closeButton>
            <Modal.Title>Order Summary</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <p>Your order details:</p>
                <div className="order-summary">
                {
                }
                {/* {this.props.selcteditems.map((item) => 
                    <div className="">
                        <div className="product-and-quantity">{item.name} X {item.quantity}</div>
                        <div className="product-price float-right">{this.getProductTotalPrice(item)}</div>
                    </div>
                )
                } */}
                </div>
            </Modal.Body>
            <Modal.Footer>
            <Button variant="secondary" onClick={this.handleModelClose}>
                Close
            </Button>
            <Button variant="success" onClick={this.handleModelClose}>
                Place Order
            </Button>
            </Modal.Footer>
            </Modal>
        );
    }
}
export default OrderSummaryModal;
