import React, { Component } from 'react';
import "./activity.css";
import { Button} from 'react-bootstrap';
import ReactDOM from 'react-dom';

class ActivityBar extends Component {

    constructor(props) {
        super(props);
    }

    handleFilteration = (event, ops) => {
        const filterText = event.target.value;
        let filterCategoryText = this.state.filterCategoryText;
        let filterRatingText = this.state.filterRatingText;
        let filterNameText = this.state.filterNameText;
        let filterPriceText = this.state.filterPriceText;
        let array = [...this.state.allItems] || [];
        if (ops === 'category') {
            filterCategoryText = filterText;
        } else if (ops === 'rating') {
            filterRatingText = filterText;
        } else if (ops === 'price') {
            filterPriceText = filterText;
        } else {
            filterNameText = filterText;
        }
        // category filter
        array = array.filter((obj) => {
            let ok = true;

            if (filterCategoryText !== '') {
                ok = (obj.categoryName === filterCategoryText);
            }

            return ok;
        });
        // other filters
        array.forEach((obj, idx) => {
            let filteredItems = obj.items.filter((item) => {
                let ok = true;

                if (ok && filterRatingText !== '') {
                    ok = item.rating == filterRatingText;
                }

                if (ok && filterPriceText !== '') {
                    const finalPrice = this.calculatePrice(item.price, item.discount);
                    ok = (finalPrice > filterPriceText.split(",")[0] && (filterPriceText.split(",")[1] && finalPrice <= filterPriceText.split(",")[1]));
                }

                if (ok && filterNameText !== '') {
                    ok = (item.name.toLowerCase().search(filterNameText.toLowerCase()) > -1);
                }
                return ok;
            });
            obj.items = filteredItems;
            array[idx] = obj;
        });
        // set State
       // this.setState({ filterNameText, filterCategoryText, filterRatingText, filterPriceText, filteredItems: array });
        this.props.getFilteredData(array);
    }

    render() {
        return(
            <div className="activity-bar row">
                <div className="col-lg-3 col-xs-3 col-md-3">
                    <label htmlFor="categoryfilter" className="pull-left">Category based Filter : </label>&nbsp;
                    <select name="categoryfilter" className="inputBox">
                        <option value="">{'All'}</option>
                        <option value="Burgers">{'Burgers'}</option>
                        <option value="Pizzas">{'Pizzas'}</option>
                        <option value="Drinks">{'Drinks'}</option>
                    </select>
                </div>
                <div className="col-lg-3 col-xs-3 col-md-3">
                    <label htmlFor="ratingfilter" className="pull-left">Rating based Filter : </label>&nbsp;
                    <select name="ratingfilter" className="inputBox">
                        <option value="">{'All'}</option>
                        <option value="1">{'★'}</option>
                        <option value="2">{'★★'}</option>
                        <option value="3">{'★★★'}</option>
                        <option value="4">{'★★★★'}</option>
                        <option value="5">{'★★★★★'}</option>
                    </select>
                </div>

                <div className="col-lg-3 col-xs-3 col-md-3">
                    <label htmlFor="namefilter" className="pull-left">Search:</label>
                    <input name="namefilter" type="text" autoFocus placeholder="Search" className="inputBox" onChange={e => this.props.handleFilteration(e, 'name')}/>
                </div>
                <div className="col-lg-3 sucess-button col-xs-3 col-md-3">
                    <Button variant="success float-right">Order Now</Button>
                </div>
            </div>
        );
    }
}

export default ActivityBar;
