import React, { Component } from 'react';
import '../custom.css';
import StarRatingComponent from 'react-star-rating-component';

class GridCard extends Component {

    constructor(props) {
        super(props);
    }
    render() {
        let imgAltText = "img"+ this.props.index; 
        let item = this.props.item;   
        return(
        <div className="col-lg-3">
            <img className="product-image" src = {item.imgSrc} alt={imgAltText} width="100%" height="250px"/>
            <p className="product-name text-center font-weight-bold">{item.name}</p>
            <p className="product-desc text-center">{item.description}</p>
            <span>
                <StarRatingComponent 
                name="rate1" 
                editing={false}
                value={item.urseRating}
                />
            </span>
            <span className="text-right product-desc">Price : {item.price} Rs.</span>
        </div>
        );
    }
}

export default GridCard;
