import React, { Component } from 'react';
import * as api from '../Api/api';
import Spinner from "../common/Spinner";
class Dashboard extends Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [],
            loading: true
        };
    }

    componentDidMount() {
        api.getAllItems()
        .then((response) => {
            this.setState({
                items: response.data.menu,
                loading: false
            });
            console.log("items", this.state.items);
            // this.props.loadFoodItems(items.menu);
            // this.props.filterMenu(items.menu);
        })
        .catch(error => {
            console.log(error);
            // this.props.loadFoodItems([]);
            // this.props.filterMenu([]);
        });
    }

    render() {
        const renderItem = this.state.items.map((item,index) => {
            let imgalt = "img"+{index};
            return(<div className="col-lg-3">
            <img src = {item.imgSrc} alt={imgalt} width="150" height="150"/>
            </div>);
        });
        return (
            <div className="row">{
                this.state.loading ? (
                    <div className="col-lg-12 text-center"><Spinner/></div>
                ):
                (renderItem)
            }
            </div>
        );
    }
}
export default Dashboard;
