import React, { Component } from 'react';
import * as api from '../Api/api';
import Spinner from "../common/Spinner";
import Container from './container';
import "../custom.css";
class Dashboard extends Component {

    constructor(props) {
        super(props);
        this.state = {
            items: [],
            loading: true
        };
    }

    componentDidMount() {
        api.getAllItems()
        .then((response) => {
            this.setState({
                items: response.data.menu.items,
                loading: false
            });
            console.log("items", this.state.items);
            // this.props.loadFoodItems(items.menu);
            // this.props.filterMenu(items.menu);
        })
        .catch(error => {
            console.log(error);
            // this.props.loadFoodItems([]);
            // this.props.filterMenu([]);
        });
    }

    render() {
        return (
            !this.state.loading ? 
                <div className="row text-center"><Spinner/></div>
            :
            <Container items= {this.state.items}/>

        );
    }
}
export default Dashboard;
