import React, { Component } from 'react';
import GridCard from "./GridCard";
import ActivityBar from './ActivityBar/ActivityBar';
import "../custom.css";

const selectedArr = [];
class Container extends Component {

    constructor(props) {
        super(props);
        this.setState({items : this.props.items});
    }

    componentWillMount() {
        this.setState({items:this.props.items})
    }

    addToSelection = (data) => {
        selectedArr.push(data);
        console.log("Selected arrray",selectedArr);
    }
    getFilteredData =(filteredItems)=>{
        this.setState({items : filteredItems});
    }

    render() {
        const renderItem = <div className="row margin-auto">{
            this.state.items.map((foodItem,index) => 
            <GridCard
            item = {foodItem}
            index = {index}
            addToCart={this.addToSelection}
            />
            )}
         </div>
        return([
            <div className="jumborton"><ActivityBar completeItems={this.props.items}
            items={this.state.items}
            getFilteredData= {this.getFilteredData}/></div>,
            renderItem
        ]
        );
    }
}

export default Container;
