import React, { Component } from 'react';
import '../custom.css';
class GridCard extends Component {

    constructor(props) {
        super(props);
    }
    render() {
        let imgAltText = "img"+ this.props.index; 
        let item = this.props.item;   
        return(
        <div className="col-lg-3">
        <img src = {item.imgSrc} alt={imgAltText} width="100%" height="250px"/>
        <p class="product-name text-center">{item.name}</p>
        <p class="product-desc text-center">{item.description}</p>
        </div>
        );
    }
}

export default GridCard;
